INSERT INTO `news` (`id`, `title`, `intro`, `content`, `image_link`, `created`, `count_view`) VALUES
(17, 'Lộ diện phiên bản Galaxy S10+ với khả năng 5G', 'Đây là một trong các phiên bản Galaxy S10 mà Samsung ra mắt vào năm sau.', '<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 15px;\">Phi&ecirc;n bản 5G của Galaxy S10+ đ&atilde; được đồn đại trong một thời gian. Trong khi Samsung kh&ocirc;ng x&aacute;c nhận về thiết bị n&agrave;y nhưng nhiều đối t&aacute;c nh&agrave; mạng cho rằng sản phẩm đang thực sự được ph&aacute;t triển. Tuy nhi&ecirc;n, th&ocirc;ng tin mới đ&atilde; tiết lộ rằng thiết bị mang số model SM-G977, v&agrave; đ&acirc;y c&oacute; thể l&agrave; chi tiết x&aacute;c nhận về sự tồn tại của sản phẩm.</span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 15px;\">Th&ocirc;ng tin đến từ GalaxyClub, cho thấy m&ocirc; h&igrave;nh 5G được ph&aacute;t hiện với cả số model SM-G977U v&agrave; SM-G977N. Ch&uacute;ng tương ứng với c&aacute;c biến thể của Mỹ v&agrave; H&agrave;n Quốc, cho thấy Samsung đ&atilde; l&agrave;m việc chăm chỉ tr&ecirc;n điện thoại của m&igrave;nh.</span></p>\r\n<p style=\"font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 1.6; color: rgb(0, 0, 0);\">\r\n	Khi n&oacute;i đến model ch&acirc;u &Acirc;u (SM-G977F), kh&ocirc;ng c&oacute; dấu vết n&agrave;o của thiết bị được t&igrave;m thấy.&nbsp;Tuy nhi&ecirc;n, điều n&agrave;y kh&ocirc;ng c&oacute; nghĩa l&agrave; Samsung kh&ocirc;ng chuẩn bị phi&ecirc;n bản cho thị trường n&agrave;y. Phi&ecirc;n bản 5G của Galaxy S10+ dự kiến sẽ chỉ đến tay người ti&ecirc;u d&ugrave;ng sớm nhất v&agrave;o th&aacute;ng 6/2019. Điều đ&oacute; c&oacute; nghĩa phi&ecirc;n bản n&agrave;y của Galaxy S10+ sẽ sớm ph&aacute;t triển.</p>\r\n<p style=\"font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 1.6; color: rgb(0, 0, 0);\">\r\n	Một điều đ&aacute;ng ch&uacute; &yacute; nữa l&agrave; mạng 5G sẽ được thiết lập nhiều ở Mỹ v&agrave; H&agrave;n Quốc v&agrave;o thời điểm đ&oacute;, trong khi ch&acirc;u &Acirc;u thường chậm hơn khi &aacute;p dụng mạng di động thế hệ tiếp theo. V&igrave; vậy, kh&ocirc;ng bất ngờ khi Samsung n&ecirc;n ưu ti&ecirc;n c&aacute;c phi&ecirc;n bản n&agrave;y.</p>\r\n<p style=\"font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 1.6; color: rgb(0, 0, 0);\">\r\n	<img alt=\"\" src=\"https://cdn.24h.com.vn/upload/4-2018/images/2018-12-17/1545020326-957-2-1544848328-width660height440.jpg\" style=\"width: 660px; height: 440px;\" /></p>\r\n<p style=\"font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 1.6; color: rgb(0, 0, 0);\">\r\n	Biến thể 5G của Galaxy S10+ sẽ c&ugrave;ng với Galaxy S10 Lite (SM-G970), Galaxy S10 (SM-G973) v&agrave; tất nhi&ecirc;n l&agrave; Galaxy S10+ (SM-G975) tạo th&agrave;nh loạt sản phẩm được mong đợi. Ba m&ocirc; h&igrave;nh sau đều được dự kiến ra mắt tại một sự kiện v&agrave;o ng&agrave;y 20/2 trước khi bắt đầu b&aacute;n ra v&agrave;o ng&agrave;y 8/3, trong khi thời điểm ra mắt bản 5G vẫn chưa r&otilde; r&agrave;ng.</p>\r\n<p style=\"font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 1.6; color: rgb(0, 0, 0);\">\r\n	<img alt=\"\" src=\"https://cdn.24h.com.vn/upload/4-2018/images/2018-12-17/1545020326-863-3-1544848336-width660height337.jpg\" style=\"width: 660px; height: 337px;\" /></p>\r\n', 's10.jpg', 1545150938, 0),
(18, 'Samsung Galaxy S10+ chỉ có 3 camera ở mặt sau', 'Có vẻ như cách sắp xếp camera của 3 biến thể Galaxy S10 mà Samsung sắp ra mắt đã bắt đầu rõ ràng.', '<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 15px;\">Trong khi t&agrave;i khoản @OnLeaks gần đ&acirc;y tuy&ecirc;n bố Galaxy S10+ cao cấp nhất sẽ đi k&egrave;m 4 camera ở mặt sau th&igrave; b&aacute;o c&aacute;o từ @UniverseIce lại tuy&ecirc;n bố rằng sản phẩm n&agrave;y sẽ chỉ đi k&egrave;m 3 camera ph&iacute;a sau giống như biến thể Galaxy S10 th&ocirc;ng thường, c&ograve;n Galaxy S10 Lite sẽ đi k&egrave;m camera k&eacute;p. D&ugrave; thế n&agrave;o đi chăng nữa th&igrave; hệ thống camera ph&iacute;a sau của loạt Galaxy S10 cao cấp sẽ tốt hơn so với Galaxy S9+ hiện tại.</span></p>\r\n<p>\r\n	<img alt=\"\" src=\"https://cdn.24h.com.vn/upload/4-2018/images/2018-12-14/1544777691-285-2-1544724917-width660height440.jpg\" style=\"width: 660px; height: 440px;\" /></p>\r\n<p>\r\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 15px;\">B&aacute;o c&aacute;o cũng cho thấy Galaxy S10 v&agrave; S10 Lite sẽ đi k&egrave;m camera selfie đơn, rất c&oacute; thể nằm trong một lỗ tr&ograve;n tr&ecirc;n m&agrave;n h&igrave;nh hiển thị. Đối với Galaxy S10+, đ&oacute; l&agrave; hệ thống camera k&eacute;p với lỗ h&igrave;nh vi&ecirc;n thuốc.</span></p>\r\n<p style=\"font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 1.6; color: rgb(0, 0, 0);\">\r\n	Ice Universe c&oacute; vẻ kh&aacute; tự tin đối với tuy&ecirc;n bố của m&igrave;nh khi cho biết c&aacute;c tuy&ecirc;n bố gần đ&acirc;y n&oacute;i rằng Galaxy S10+ sở hữu thiết lập camera 4 ống k&iacute;nh ph&iacute;a sau đơn giản l&agrave; sai lầm. Tuy nhi&ecirc;n &ocirc;ng cũng thừa nhận rằng điều n&agrave;y kh&ocirc;ng c&oacute; nghĩa l&agrave; phi&ecirc;n bản đặc biệt thứ 4 sẽ kh&ocirc;ng c&oacute; thiết lập n&agrave;y. Về cơ bản đ&oacute; l&agrave; phi&ecirc;n bản hỗ trợ mạng 5G của Galaxy S10.</p>\r\n<p style=\"font-family: Arial, Helvetica, sans-serif; font-size: 15px; line-height: 1.6; color: rgb(0, 0, 0);\">\r\n	Li&ecirc;n quan đến biến thể đặc biệt n&agrave;y, ngo&agrave;i c&ocirc;ng nghệ 5G th&igrave; b&aacute;o c&aacute;o cho biết sản phẩm c&oacute; thể đi k&egrave;m ống k&iacute;nh thứ 4 cho camera ph&iacute;a sau, v&agrave; đ&oacute; l&agrave; loại cảm biến 3D si&ecirc;u ti&ecirc;n tiến (3D ToF), kết hợp kh&ocirc;ng gian lưu trữ bộ nhớ trong l&ecirc;n đến 1 TB.</p>\r\n', 's10_3_camera.jpg', 1545151050, 0);

